import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button = (props) => {
  return(
    <div>
      <button onClick={props.function}>{text}</button>
    </div>
  )
}

const Statistics = (props) => {


  return(
   <p>{props.text}{props.value}</p>
  )
}

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const all={
    good:good,
    neutral:neutral,
    bad:bad,
    total:good+neutral+bad
  }

  const average = ((all.good*1)+(all.neutral*0)+(all.bad*(-1)))/(all.total);
  const positive = ((all.good*100)/all.total)+'%';

  const goodClick= () => {
    setGood(good+1);
  }

  const neutralClick= () => {
    setNeutral(neutral+1);
  }

  const badClick= () => {
    setBad(bad+1);
  }
  if(all.total===0){
    return(
      <p>No feedback given</p>
    )
  }
  return (
    <div>
      <h2>Give feedback</h2>
      <Button function={goodClick} text="good"/>
      <Button function={neutralClick} text="neutral"/>
      <Button function={badClick} text="bad"/>
      <h2>Statistics</h2>
      <Statistics text="good " value={good}/>
      <Statistics text="neutral " value={neutral}/>
      <Statistics text="bad " value={bad}/>
      <Statistics text="all " value={all.total}/>
      <Statistics text="average " value={average}/>
      <Statistics text="positive " value={positive}/>
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
