import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const All = ({all}) => {
  return(
    <p>all {all.good+all.neutral+all.bad}</p>
)
}

const Average = ({all}) => {
  const average=((all.good*1)+(all.neutral*0)+(all.bad*(-1)))/(all.good+all.neutral+all.bad);

  return(
    <p>average {average}</p>
  )
}
const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const all={
    good:good,
    neutral:neutral,
    bad:bad
  }

  const goodClick= () => {
    setGood(good+1);
  }

  const neutralClick= () => {
    setNeutral(neutral+1);
  }

  const badClick= () => {
    setBad(bad+1);
  }

  return (
    <div>
      <h2>Give feedback</h2>
      <button onClick={goodClick}>good</button>
      <button onClick={neutralClick}>neutral</button>
      <button onClick={badClick}>bad</button>
      <h2>Statistics</h2>
      <p>good {good}</p>
      <p>neutral {neutral}</p>
      <p>bad {bad}</p>
      <All all={all}/>
      <Average all={all}/>
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
